:0;a:5:{s:4:"data";s:40:"http://weblogtoolscollection.com/?p=9651";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:73:"http://feedproxy.google.com/~r/weblogtoolscollection/UXMP/~3/xZ7zPDN_th8/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:1720:"<h3>New plugins</h3>\n<p><a href="http://rampantlogic.com/amazonx/"><strong>Amazon Express</strong></a> allows you to easily insert Amazon Associates links and Amazon product images, including images of book covers, into your WordPress posts.</p>\n<p><a href="http://wordpress.org/extend/plugins/youtubenails/"><strong>YoutubeNails</strong></a> creates a dynamic gallery of thumbnails of each post with YouTube videos displayed on the content linked to the post permalink.</p>\n<h3>Updated plugins</h3>\n<p><a href="http://www.cleancode.co.nz/cleancodenz-exclude-pages-wordpress-plugin"><strong>CleanCodeNZ Exclude Pages</strong></a> allows you to define the custom field to be attached to a page to be excluded from navigation and(or) search results.</p>\n<p><a href="http://wordpress.org/extend/plugins/dukapress/"><strong>DukaPress</strong></a> is an open source and free to use e-commerce platform.</p>\n<p><a href="http://sudarmuthu.com/wordpress/easy-retweet"><strong>Easy ReTweet</strong></a> is a WordPress Plugin which lets you add retweet or Tweet this buttons for your WordPress posts, together with the retweet count.</p>\n<p><a href="http://wordpress.org/extend/plugins/simple-footnotes/"><strong>Simple Footnotes</strong></a> allows you to create simple, elegant footnotes on your site. Use the [ref] shortcode and the plugin takes care of the rest.</p>\n<p><a href="http://sudarmuthu.com/wordpress/twitter-avatar-reloaded"><strong>Twitter Avatar Reloaded</strong></a> adds a new field to the comment form for the user’s Twitter username and uses it to replace the Gravatar with their Twitter avatar.</p>\n<img src="http://feeds.feedburner.com/~r/weblogtoolscollection/UXMP/~4/xZ7zPDN_th8" height="1" width="1" />";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Tue, 08 Feb 2011 14:00:38 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:10:"James Huff";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:12;a:6:{s:4:"data";s:13:"\n	\n	\n	\n	\n	\n	\n";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:61:"Weblog Tools Collection: WordPress 3.0.5 and 3.1-RC4 Released";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:40:"http://weblogtoolscollection.com/?p=9653";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:73:"http://feedproxy.google.com/~r/weblogtoolscollection/UXMP/~3/cBATc9KtKcs/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:819:"<p><a href="http://wordpress.org/">WordPress</a> 3.0.5 and 3.1-RC4 <a href="http://wordpress.org/news/2011/02/wordpress-3-0-5/">have been released</a>.</p>\n<p>Both releases address three security issues and add additional security enhancements, and 3.1-RC4 fixes &#8220;about two dozen additional bugs.&#8221;</p>\n<p>Both updates are available immediately via your Dashboard, but users updating to 3.0.5 will need to update to the latest release of Akismet again. Core developer <a href="http://andrewnacin.com/">Andrew Nacin</a> hopes to minimize &#8220;<a href="https://twitter.com/nacin/status/34875043271942144">the Akismet update dance</a>&#8221; in WordPress 3.1 and put an end to it in WordPress 3.2.</p>\n<img src="http://feeds.feedburner.com/~r/weblogtoolscollection/UXMP/~4/cBATc9KtKcs" height="1" width="1" />";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Tue, 08 Feb 2011 07:43:45 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:10:"James Huff";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:13;a:6:{s:4:"data";s:13:"\n	\n	\n	\n	\n	\n	\n";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:54:"WordPress.tv: David Coveney – WordPress In Big Media";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:27:"http://wordpress.tv/?p=5309";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:54:"http://wordpress.tv/2010/07/17/wordpress-in-big-media/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:1760:"<div id="x-video-51" class="video-player">\n</div>\n<br />  <a rel="nofollow" href="http://feeds.wordpress.com/1.0/gocomments/wptv.wordpress.com/5309/"><img alt="" border="0" src="http://feeds.wordpress.com/1.0/comments/wptv.wordpress.com/5309/" /></a> <a rel="nofollow" href="http://feeds.wordpress.com/1.0/godelicious/wptv.wordpress.com/5309/"><img alt="" border="0" src="http://feeds.wordpress.com/1.0/delicious/wptv.wordpress.com/5309/" /></a> <a rel="nofollow" href="http://feeds.wordpress.com/1.0/gofacebook/wptv.wordpress.com/5309/"><img alt="" border="0" src="http://feeds.wordpress.com/1.0/facebook/wptv.wordpress.com/5309/" /></a> <a rel="nofollow" href="http://feeds.wordpress.com/1.0/gotwitter/wptv.wordpress.com/5309/"><img alt="" border="0" src="http://feeds.wordpress.com/1.0/twitter/wptv.wordpress.com/5309/" /></a> <a rel="nofollow" href="http://feeds.wordpress.com/1.0/gostumble/wptv.wordpress.com/5309/"><img alt="" border="0" src="http://feeds.wordpress.com/1.0/stumble/wptv.wordpress.com/5309/" /></a> <a rel="nofollow" href="http://feeds.wordpress.com/1.0/godigg/wptv.wordpress.com/5309/"><img alt="" border="0" src="http://feeds.wordpress.com/1.0/digg/wptv.wordpress.com/5309/" /></a> <a rel="nofollow" href="http://feeds.wordpress.com/1.0/goreddit/wptv.wordpress.com/5309/"><img alt="" border="0" src="http://feeds.wordpress.com/1.0/reddit/wptv.wordpress.com/5309/" /></a> <img alt="" border="0" src="http://stats.wordpress.com/b.gif?host=wordpress.tv&blog=5089392&post=5309&subd=wptv&ref=&feed=1" width="1" height="1" /><div><a href="http://wordpress.tv/2010/07/17/wordpress-in-big-media/"><img alt="" src="http://videos.videopress.com/FRniUxUH/wp-in-big-media_qt-1024x576-6mbs_001_std.original.jpg" width="160" height="120" /></a></div>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Tue, 08 Feb 2011 05:30:09 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:12:"Michael Pick";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:14;a:6:{s:4:"data";s:13:"\n	\n	\n	\n	\n	\n	\n";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:55:"Dev Blog: WordPress 3.0.5 (and 3.1 Release Candidate 4)";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:33:"http://wordpress.org/news/?p=1683";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:50:"http://wordpress.org/news/2011/02/wordpress-3-0-5/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:3673:"<p><a href="http://wordpress.org/download/">WordPress 3.0.5</a> is now available and is a security hardening update for all previous WordPress versions.</p>\n<p><a href="http://codex.wordpress.org/Version_3.0.5">This security release</a> is required if you have any untrusted user accounts, but it also comes with important security enhancements and hardening. All WordPress users are strongly encouraged to update.</p>\n<p>Three point oh point five<br />\nEnhances security<br />\nThree point one comes soon</p>\n<p>The release addresses a number of issues and provides two additional enhancements:</p>\n<p><strong>Two moderate security issues</strong> were fixed that could have allowed a Contributor- or Author-level user to gain further access to the site.</p>\n<p><strong>One information disclosure issue</strong> was addressed that could have allowed an Author-level user to view contents of posts they should not be able to see, such as draft or private posts.</p>\n<p><strong>Two security enhancements</strong> were added. One improved the security of any plugins which were not properly leveraging our security API. The other offers additional defense in depth against a vulnerability that was fixed in previous release.</p>\n<p>Thanks to Nils Jueneman and Saddy for their private and responsible disclosures to security@wordpress.org for two of the issues. The others were reported or repaired by our security team.</p>\n<p><strong><a href="http://wordpress.org/download/">Download 3.0.5</a> or update automatically from the Dashboard &gt; Updates menu in your site&#8217;s admin area. Please update immediately.</strong></p>\n<hr />\n<h3>WordPress 3.1 Release Candidate 4 is also now available.</h3>\n<p>The Release Candidate 4  build includes the security fixes and enhancements included in 3.0.5 and addresses about two dozen additional bugs. This includes fixes for:</p>\n<ul>\n<li>Deleting a user and reassigning their posts to another user.</li>\n<li>Marking multiple users or sites as spam in multisite.</li>\n<li>PHP4 compatibility.</li>\n</ul>\n<p>As outlined in <a href="http://wordpress.org/news/2011/01/wordpress-3-1-release-candidate-3/">previous</a> <a href="http://wordpress.org/news/2011/01/wordpress-3-1-release-candidate-2/">RC</a> <a href="http://wordpress.org/news/2010/12/wordpress-3-1-release-candidate/">posts</a>, if you are testing the release candidate and think you&#8217;ve found a bug, there are a few ways to let us know:</p>\n<ul>\n<li>Post it to the <a href="http://wordpress.org/support/forum/alphabeta/">Alpha/Beta area in the support forums</a></li>\n<li>Report it to the <a href="http://lists.automattic.com/mailman/listinfo/wp-testers">wp-testers mailing list</a></li>\n<li>Join the development IRC channel and tell us live at irc.freenode.net #wordpress-dev</li>\n<li>File a bug ticket on the <a href="http://core.trac.wordpress.org/">WordPress Trac</a></li>\n</ul>\n<p>To test WordPress 3.1, try the <a href="http://wordpress.org/extend/plugins/wordpress-beta-tester/">WordPress Beta Tester plugin</a> (you&#8217;ll want &#8220;bleeding edge nightlies&#8221;). Or you can <a href="http://wordpress.org/wordpress-3.1-RC4.zip">download the release candidate here</a> (zip). If any new issues become known, you&#8217;ll be able to <a href="http://core.trac.wordpress.org/report/6">find them here</a>.</p>\n<p>After nearly five months of development and testing, we think we&#8217;re very close to a final release. <strong>Users and developers, please test your themes and plugins.</strong></p>\n<p>Download <a href="http://wordpress.org/wordpress-3.1-RC4.zip">WordPress 3.1 RC4</a> or <a href="http://wordpress.org/download/">WordPress 3.0.5</a> now.</p>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Mon, 07 Feb 2011 23:29:50 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:12:"Andrew Nacin";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:15;a:6:{s:4:"data";s:13:"\n	\n	\n	\n	\n	\n	\n";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:55:"Alex King: Crowd Favorite Hiring Developers and Interns";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:27:"http://alexking.org/?p=5293";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:76:"http://alexking.org/blog/2011/02/07/crowd-favorite-hiring-developers-interns";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:2905:"<p>My company, <a href="http://crowdfavorite.com">Crowd Favorite</a> is hiring.</p>\n<p>We&#8217;re hiring 3-5 developers, right now. We&#8217;ve listed the <a href="http://crowdfavorite.com/jobs/">available positions</a> as: Web Developer, Experienced Web Developer, Developer Intership and Designer/Front-end Developer Internship. We list specific positions because it helps people identify positions they think they can fill, but in reality our hiring approach is really simple: we hire smart people who love to build great things for the web.</p>\n<p class="center"><a href="http://crowdfavorite.com/jobs/"><img src="http://alexking.org/wp/wp-content/uploads/2011/02/hiring-for-ak.png" alt="Crowd Favorite is Hiring" /></a></p>\n<p>We&#8217;ll hire 3-5 people over the next little bit and it&#8217;s likely their skill sets will intersect somewhat with the positions we&#8217;ve got listed on the website, but we&#8217;re not looking to fit people into boxes. The strengths and abilities of the people we bring on will define their roles with us. We like to put people in position to succeed.</p>\n<p>I think Crowd Favorite is a pretty unique place to work. We&#8217;ve got a small team (~12-15 people) with a strong technical focus, little &#8220;overhead&#8221; and no internal politics. It&#8217;s a true developer-centric environment.</p>\n<p>We get the privilege of working with wonderful clients on interesting and challenging projects. By the nature of our consulting and custom development business, we have the opportunity to work in a wide variety of technical areas. We do a lot of really interesting things with WordPress, and we also get to play with a lot of web services, create web-based applications, etc., etc. We tackle problems ranging from scaling back-end services to optimizing front-end delivery and user experience.</p>\n<p>Our basic criteria for taking on projects is: &#8220;will this be fun and/or interesting and/or challenging?&#8221; Trust me, you won&#8217;t get bored.</p>\n<p>We also create our own products &#8211; some internal and some that we make commercially available (expect a few more product releases as soon as we can package them up). The opportunity to work on both well-defined, specific problems and built long term products is a really great way to make sure we take a pragmatic approach to both.</p>\n<p>The work is demanding and you will be expected to understand what you need to do and largely manage your own time and deliverables &#8211; we don&#8217;t have babysitters. If you want an opportunity to learn and grow, to work with a team of great people, smart people, I want to hear from you.</p>\n<p>All of these positions are available in our Denver office. The non-intern positions are all full time with benefits. And you&#8217;ll get a pretty cool t-shirt.</p>\n<img src="http://alexking.org/wp/?ak_action=api_record_view&id=5293&type=feed" alt="" />";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Mon, 07 Feb 2011 20:39:00 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:4:"Alex";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:16;a:6:{s:4:"data";s:13:"\n	\n	\n	\n	\n	\n	\n";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:44:"WordPress Podcast: How To Monetize Your Blog";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:30:"http://wp-community.org/?p=860";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:54:"http://wp-community.org/2011/02/07/monetize-your-blog/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:2505:"<p>So, you want to be a professional blogger? But what exactly do you need to do to make money from this? This week we take a deeper look at how to start generating revenue from your blog. Whether you have a fledgling WordPress blog or are a well seasoned blogger with an entire community built up, there are a number of avenues you can go down. But which is best? And how do you <em>actually</em> make it all happen?</p><ul><li>There are always some growing pains as every new blog strives to get its feet off the ground, not least of which is trying to generate revenue. We start off this week by taking a closer look at the stages of development a site goes through as it matures.</li><li>Joost gives an insight into the journey of his own site, <a href="http://yoast.com/">Yoast.com</a>, and gets down to the nitty gritty of what worked for him and what didn’t.</li><li>Joost lifts the lid on <a href="http://www.google.com/AdSense">Google AdSense</a>, and explains its pros and cons.</li><li>Affiliate marketing; we explain the importance of having both high readership <em>and</em> the trust of those readers. We recommend taking a look at <a href="http://www.pepperjamnetwork.com/">PepperJamNetwork</a> and <a href="http://www.cj.com/">Commission Junction</a>.</li><li>We explore other avenues within traditional banner advertising, including some surprising and very neat little hints and tips!</li><li>The value of the personal brand: In the long haul, this could be the most beneficial thing you do. We explain the how and the why. Joost tells us how his blog has lead to consultancy work for some top customers.</li><li>Frederick talks through the benefits of monetising old blog content by selling it as an eBook. Producing products that can be ‘built’ once and sold multiple times has huge benefits.</li><li>The importance of <em>scarcity</em>!</li><li>Who you take advertising from is important – if they are ads for less than respectable services or products it could be incredibly detrimental to your personal brand and the reputation of your blog. There is no escaping accountability, and especially not on the web!</li><li>Why you should tread carefully when asking your readers for money.</li></ul><p>The bottom line? Make money, but always keep your readers in mind!</p><p>Some useful links:</p><ul><li><a href="http://mashable.com/">Mashable.com</a></li><li><a href="http://www.css3.info/">css3.info</a></li><li><a href="http://www.chrisbrogan.com/">Chris Brogan</a></li></ul>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Mon, 07 Feb 2011 19:48:59 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:13:"Joost de Valk";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:17;a:6:{s:4:"data";s:13:"\n	\n	\n	\n	\n	\n	\n";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:57:"Weblog Tools Collection: WordPress Theme Releases for 2/6";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:40:"http://weblogtoolscollection.com/?p=9645";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:73:"http://feedproxy.google.com/~r/weblogtoolscollection/UXMP/~3/bmd_Nu6NUu8/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:1614:"<p><img class="alignnone size-full wp-image-9646" title="begi" src="http://weblogtoolscollection.com/wp-content/uploads/2011/02/begi.jpg" alt="" width="150" height="113" /></p>\n<p><a href="http://dukapress.com/blog/2011/02/05/begi-wordpress-e-commerce-theme/"><strong>Begi</strong></a> is a clean, minimal and highly customizable WordPress e-commerce theme.</p>\n<p><img class="alignnone size-full wp-image-9647" title="nichewp" src="http://weblogtoolscollection.com/wp-content/uploads/2011/02/nichewp.jpg" alt="" width="150" height="113" /></p>\n<p><a href="http://www.nichewp.com/nichewp-download-fastest-wordpress-theme.html"><strong>NicheWP</strong></a> is a two-column WordPress theme with an elegant design and search engine and Adsense optimization.</p>\n<p><img class="alignnone size-full wp-image-9648" title="thatlivemusic" src="http://weblogtoolscollection.com/wp-content/uploads/2011/02/thatlivemusic.jpg" alt="" width="150" height="113" /></p>\n<p><a href="http://www.thatagency.com/solutions/blog-templates.php"><strong>THAT Live Music</strong></a> is perfect for music bloggers and music news websites.</p>\n<p><img class="alignnone size-full wp-image-9649" title="torn" src="http://weblogtoolscollection.com/wp-content/uploads/2011/02/torn.jpg" alt="" width="150" height="113" /></p>\n<p><a href="http://wordpress.org/extend/themes/torn"><strong>Torn</strong></a> is a visually exciting WordPress theme with the Navigation bar and Header image tearing on to the screen after opening the page.</p>\n<img src="http://feeds.feedburner.com/~r/weblogtoolscollection/UXMP/~4/bmd_Nu6NUu8" height="1" width="1" />";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Sun, 06 Feb 2011 14:00:11 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:10:"James Huff";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:18;a:6:{s:4:"data";s:13:"\n	\n	\n	\n	\n	\n	\n";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:62:"Weblog Tools Collection: WordPress.com Launches Premium Themes";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:40:"http://weblogtoolscollection.com/?p=9639";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:73:"http://feedproxy.google.com/~r/weblogtoolscollection/UXMP/~3/srVPLHC3Hi0/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:1523:"<p><a href="http://wordpress.com/">WordPress.com</a> has just <a href="http://en.blog.wordpress.com/2011/02/03/introducing-premium-themes/">launched their premium theme store</a>, bringing premium theme developers and the first two of hopefully many premium themes to <a href="http://wordpress.com/stats/">over 17 million</a> WordPress.com blogs.</p>\n<p>Previously, WordPress.com users were limited to <a href="http://theme.wordpress.com/">just over a hundred free themes</a> with no ability to upload their own, thus cutting them off from the premium theme market. Now, premium theme developers have a chance to cash in on &#8220;the other WordPress&#8221; users.</p>\n<p>WordPress.com is starting their premium theme offerings with <a href="http://theme.wordpress.com/themes/headlines/">Headlines</a> for $45 (<a href="http://www.woothemes.com/2009/11/headlines/">available to self-hosted WordPress users</a> for $70 with two &#8220;bonus&#8221; themes) and <a href="http://theme.wordpress.com/themes/shelf/">Shelf</a> for $68 (also <a href="http://thethemefoundry.com/shelf/">available to self-hosted WordPress users</a> for $68). WordPress.com plans to increase their free and premium theme offerings &#8220;in a big way&#8221; this year, so this is definitely shaping up to be a game-changer in the premium theme market.</p>\n<p>(Disclosure: I also work at <a href="http://automattic.com/">Automattic</a>.)</p>\n<img src="http://feeds.feedburner.com/~r/weblogtoolscollection/UXMP/~4/srVPLHC3Hi0" height="1" width="1" />";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Sat, 05 Feb 2011 14:00:49 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:10:"James Huff";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:19;a:6:{s:4:"data";s:13:"\n	\n	\n	\n	\n	\n	\n";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:20:"Matt: WP Board Game?";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:21:"http://ma.tt/?p=37414";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:35:"http://ma.tt/2011/02/wp-board-game/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:214:"<p><a href="http://wordcampindonesia.com/2011/01/25/wordcamp-indonesia-proudly-presents-the-first-wordpress-board-game/">WordCamp Indonesia proudly presents the first WordPress Board Game</a>. I gotta try this.</p>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Sat, 05 Feb 2011 03:11:30 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:4:"Matt";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:20;a:6:{s:4:"data";s:13:"\n	\n	\n	\n	\n	\n	\n";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:25:"Lloyd: Ma.tt: not a robot";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:30:"http://foolswisdom.com/?p=2963";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:45:"http://foolswisdom.com/photomatt-not-a-robot/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:1467:"<p>Even after working for <a href="http://ma.tt/">Matt Mullenweg</a> for over four years now (my longest job!), it still totally pumps me up how forward thinking, thoughtful, and human Matt is.</p>\n<p>From the ThemeShaper article &#8220;<a href="http://themeshaper.com/dotcom-premium-themes/">Premium Themes on WP.com, the backstory</a>&#8220;:</p>\n<blockquote><p>&#8220;&#8230;it became obvious to me that we had to figure out the GPL issues first so introducing a WP.com marketplace wouldn’t inadvertently harm the WordPress community by sucking the air out of .org theme development, so I held off the revenue and success we knew this would bring to work out the GPL issues out with the community.</p>\n<p>&#8230;</p>\n<p>But very explicitly this is an experiment. We’re not psychic and there are many open questions: Will anyone buy these things? How will the private forums work for support, both for our users and partners? How long does it take us to review and get a new theme online? What’s the most effective price ranges? How many themes and partners should we have? How do we promote the premium themes, while balancing adding new free ones? Will any of them ever be more popular than the Smoothie? (51,109 blogs and counting.)</p></blockquote>\n<p>Go read <a href="http://themeshaper.com/dotcom-premium-themes/" rel="nofollow">the full article</a>.</p>\n<p>Could WordPress have a better <acronym title="Benevolent Dictator For Life">BDFL</acronym>?</p>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Fri, 04 Feb 2011 18:08:00 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:5:"Lloyd";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:21;a:6:{s:4:"data";s:13:"\n	\n	\n	\n	\n	\n	\n";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:58:"Weblog Tools Collection: WordPress Plugin Releases for 2/4";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:40:"http://weblogtoolscollection.com/?p=9637";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:73:"http://feedproxy.google.com/~r/weblogtoolscollection/UXMP/~3/2ngemjJ5MSU/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:878:"<h3>New plugins</h3>\n<p><a href="http://www.alleba.com/blog/2011/02/01/wordpress-plugin-piclyf/"><strong>Piclyf</strong></a> displays user&#8217;s picture stream utilizing Piclyf.com&#8217;s API.</p>\n<p><a href="http://wordpress.org/extend/plugins/gameplorers-wpcolorbox/"><strong>WPColorBox</strong></a> adds a ColorBox effect to all of your linked images automatically.</p>\n<h3>Updated plugins</h3>\n<p><a href="http://www.dynamicwp.net/plugins/dynamicwp-popup-menu/"><strong>DynamicWP Pop-Up Menu</strong></a> will put your menu icons in a stack, placed in the bottom right corner of your page.</p>\n<p><a href="http://wordpress.org/extend/plugins/eshop/"><strong>eShop</strong></a> is an accessible shopping cart plugin for WordPress, packed with various features.</p>\n<img src="http://feeds.feedburner.com/~r/weblogtoolscollection/UXMP/~4/2ngemjJ5MSU" height="1" width="1" />";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Fri, 04 Feb 2011 14:00:30 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:10:"James Huff";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:22;a:6:{s:4:"data";s:13:"\n	\n	\n	\n	\n	\n	\n";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:23:"Matt: Premium Backstory";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:21:"http://ma.tt/?p=37412";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:39:"http://ma.tt/2011/02/premium-backstory/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:205:"<p>Over on ThemeShaper I share <a href="http://themeshaper.com/dotcom-premium-themes/">Premium Themes on WP.com, the backstory</a>, which also links to all of the coverage from our announcement today.</p>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Fri, 04 Feb 2011 07:11:24 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:4:"Matt";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:23;a:6:{s:4:"data";s:13:"\n	\n	\n	\n	\n	\n	\n";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:47:"Weblog Tools Collection: WordPress Android Apps";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:40:"http://weblogtoolscollection.com/?p=9631";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:73:"http://feedproxy.google.com/~r/weblogtoolscollection/UXMP/~3/GCA511zGGqw/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:2614:"<p><a href="http://wordpress.org/">WordPress</a> is incredibly popular, and there are a few <a href="http://en.wikipedia.org/wiki/Android_(operating_system)">Android</a> apps out there written specifically for WordPress, but certainly not <a href="http://weblogtoolscollection.com/archives/2011/01/30/wordpress-ios-apps/">as many as the there are for iOS</a>.</p>\n<ul>\n<li><a href="http://android.wordpress.org/"><strong>WordPress for Android</strong></a> &#8211; Of course, you have to start off the list with this app, the official WordPress app from <a href="http://automattic.com/">Automattic</a>. &#8220;WordPress for Android is an Open Source app that empowers you to write new posts, edit content, and manage comments with built-in notifications.&#8221; Requires a self-hosted WordPress or a <a href="http://wordpress.com/">WordPress.com</a> blog. (<a href="http://market.android.com/details?id=org.wordpress.android">Android Market</a>)</li>\n<li><a href="http://nickthecook.wordpress.com/postbot/"><strong>PostBot</strong></a> &#8211; &#8220;PostBot is an open-source WordPress client and supports multiple images in a post with thumbnail previews, sharing of images directly from Camera and Picture apps, editing existing posts, saving drafts locally, adding categories to a blog, and multiple blogs.&#8221; Requires a self-hosted WordPress or a <a href="http://wordpress.com/">WordPress.com</a> blog. (<a href="http://market.android.com/details?id=org.hopto.group18.postbot">Android Market</a>)</li>\n<li><a href="http://www.kreci.net/code/wp-stats-for-android/"><strong>WP Stats</strong></a> &#8211; &#8220;Wp Stats is an app for your Android device that helps you to access your WordPress traffic stats collected by the <a href="http://wordpress.org/extend/plugins/stats/">WordPress.com Stats plugin</a>. It is much faster way of reading all important data about your traffic blog than opening it in a web browser.&#8221; Requires the WordPress.com Stats plugin or a <a href="http://wordpress.com/">WordPress.com</a> blog. (<a href="http://market.android.com/details?id=net.kreci.wpstats">Android Market</a>)</li>\n</ul>\n<p>As you can see, this list is rather short. I&#8217;ve been hunting for WordPress-related Android apps for the last four days, and besides some incredibly low-rated copies of WordPress for Android, this is it.</p>\n<p>In other words, we need your help! Do you use an Android app with WordPress that we&#8217;ve neglected to list? If so, we&#8217;d love to hear about it!</p>\n<img src="http://feeds.feedburner.com/~r/weblogtoolscollection/UXMP/~4/GCA511zGGqw" height="1" width="1" />";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Thu, 03 Feb 2011 14:00:19 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:10:"James Huff";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:24;a:6:{s:4:"data";s:13:"\n	\n	\n	\n	\n	\n	\n";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:57:"Weblog Tools Collection: WordPress Theme Releases for 2/2";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:40:"http://weblogtoolscollection.com/?p=9621";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:73:"http://feedproxy.google.com/~r/weblogtoolscollection/UXMP/~3/HUaLR_Zk5CA/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:1865:"<p><img class="alignnone size-full wp-image-9622" title="altisfx" src="http://weblogtoolscollection.com/wp-content/uploads/2011/02/altisfx.jpg" alt="" width="150" height="113" /></p>\n<p><a href="http://wordpress.org/extend/themes/altis-fx"><strong>Altis fx</strong></a> is clean and simple. XHTML/CSS code is optimized and search engine friendly. Sidebar is fixed at the top of the screen (when window height is less than sidebar height).</p>\n<p><img class="alignnone size-full wp-image-9623" title="applezoonextended" src="http://weblogtoolscollection.com/wp-content/uploads/2011/02/applezoonextended.jpg" alt="" width="150" height="113" /></p>\n<p><a href="http://www.blogging-inside.de/kostenloses-applezoomextended-theme-fuer-wordpress-3-0_23650.html"><strong>AppleZoomExtended</strong></a> is 1000px wide with 2 columns, 9 widget areas, and about 40 theme options. The sidebar is designed for 300Px Adsense Ads.</p>\n<p><img class="alignnone size-full wp-image-9624" title="retrostripes" src="http://weblogtoolscollection.com/wp-content/uploads/2011/02/retrostripes.jpg" alt="" width="150" height="113" /></p>\n<p><a href="http://www.themepals.com/our-themes/retro-stripes"><strong>Retro Stripes</strong></a> is a blog-style layout with a portfolio and contact form. Best for bloggers, designers and photographers.</p>\n<p><img class="alignnone size-full wp-image-9625" title="zeecompany" src="http://weblogtoolscollection.com/wp-content/uploads/2011/02/zeecompany.jpg" alt="" width="150" height="113" /></p>\n<p><a href="http://themezee.com/zeeCompany-free/"><strong>zeeCompany</strong></a> is a simple and clean 2 column theme with great features: Colorpicker, Content Slider, Shortcodes, Dropdown Navigation, Costum Widgets (Tabs, Twitter), and more.</p>\n<img src="http://feeds.feedburner.com/~r/weblogtoolscollection/UXMP/~4/HUaLR_Zk5CA" height="1" width="1" />";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Wed, 02 Feb 2011 14:00:28 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:10:"James Huff";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:25;a:6:{s:4:"data";s:13:"\n	\n	\n	\n	\n	\n	\n";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:55:"Mark Jaquith: Customize the WordPress Login Screen Logo";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:39:"http://markjaquith.wordpress.com/?p=559";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:78:"http://markjaquith.wordpress.com/2011/02/01/customized-wordpress-login-screen/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:3457:"<p>I&#8217;ve customized the WordPress login screen for several of my clients. It is a nice professional touch that goes a long way toward making a site feel like &#8220;home.&#8221; Today, I&#8217;ve released a plugin called <a href="http://wordpress.org/extend/plugins/login-logo/">Login Logo</a> [<a href="http://coveredwebservices.com/wp-plugin-install/?plugin=login-logo">install</a>] that lets you easily do the same. There are other plugins that let you customize the login screen logo. But this one works with <strong>zero configuration</strong>. You just create the logo (PNG image with a transparent background), name it <code>login-logo.png</code>, and drop it into your <code>/wp-content/</code> directory.</p>\n<p>That&#8217;s it.</p>\n<div id="attachment_560" class="wp-caption aligncenter"><img src="http://markjaquith.files.wordpress.com/2011/02/screenshot-1.png?w=420&h=365" alt="" title="screenshot-1" width="420" height="365" class="size-full wp-image-560" /><p class="wp-caption-text">Here is an example modified login form</p></div>\n<p>The plugin takes care of generating the correct CSS, based on the image&#8217;s size. For best results, use an image that is less than 326 pixels wide.</p>\n<div id="attachment_561" class="wp-caption aligncenter"><img src="http://markjaquith.files.wordpress.com/2011/02/screenshot-2.png?w=483&h=329" alt="" title="screenshot-2" width="483" height="329" class="size-full wp-image-561" /><p class="wp-caption-text">The source image that produced the above login screen</p></div>\n<h2>Install &#8220;Login Logo&#8221;</h2>\n<p>Click this link: <a href="http://coveredwebservices.com/wp-plugin-install/?plugin=login-logo">Install the &#8220;Login Logo&#8221; plugin</a>.</p>\n<p>Alternatively, you can download it from <a href="http://wordpress.org/extend/plugins/login-logo/">the WordPress plugins directory</a>.</p>\n<br />  <a rel="nofollow" href="http://feeds.wordpress.com/1.0/gocomments/markjaquith.wordpress.com/559/"><img alt="" border="0" src="http://feeds.wordpress.com/1.0/comments/markjaquith.wordpress.com/559/" /></a> <a rel="nofollow" href="http://feeds.wordpress.com/1.0/godelicious/markjaquith.wordpress.com/559/"><img alt="" border="0" src="http://feeds.wordpress.com/1.0/delicious/markjaquith.wordpress.com/559/" /></a> <a rel="nofollow" href="http://feeds.wordpress.com/1.0/gofacebook/markjaquith.wordpress.com/559/"><img alt="" border="0" src="http://feeds.wordpress.com/1.0/facebook/markjaquith.wordpress.com/559/" /></a> <a rel="nofollow" href="http://feeds.wordpress.com/1.0/gotwitter/markjaquith.wordpress.com/559/"><img alt="" border="0" src="http://feeds.wordpress.com/1.0/twitter/markjaquith.wordpress.com/559/" /></a> <a rel="nofollow" href="http://feeds.wordpress.com/1.0/gostumble/markjaquith.wordpress.com/559/"><img alt="" border="0" src="http://feeds.wordpress.com/1.0/stumble/markjaquith.wordpress.com/559/" /></a> <a rel="nofollow" href="http://feeds.wordpress.com/1.0/godigg/markjaquith.wordpress.com/559/"><img alt="" border="0" src="http://feeds.wordpress.com/1.0/digg/markjaquith.wordpress.com/559/" /></a> <a rel="nofollow" href="http://feeds.wordpress.com/1.0/goreddit/markjaquith.wordpress.com/559/"><img alt="" border="0" src="http://feeds.wordpress.com/1.0/reddit/markjaquith.wordpress.com/559/" /></a> <img alt="" border="0" src="http://stats.wordpress.com/b.gif?host=markjaquith.wordpress.com&blog=316&post=559&subd=markjaquith&ref=&feed=1" width="1" height="1" />";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Tue, 01 Feb 2011 23:12:35 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:12:"Mark Jaquith";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:26;a:6:{s:4:"data";s:13:"\n	\n	\n	\n	\n	\n	\n";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:23:"Matt: Update from Cairo";s:7:"attrib